import { useContext } from "react";
import { UserContext } from "../userContext";
import { Link } from "react-router-dom";

function Header(props) {
    const userContext = useContext(UserContext);
   
    // Custom button style that combines Link and button appearance
    const buttonStyle = "px-4 py-2 rounded no-underline text-black inline-block text-center hover:bg-opacity-80 transition-colors";
   
    return (
        <header className="bg-white shadow-sm p-4">
            <div className="container mx-auto flex flex-col md:flex-row justify-between items-center">
                <h1 className="text-2xl font-bold mb-4 md:mb-0">{props.title}</h1>
               
                <nav>
                    <div className="flex flex-wrap gap-2">
                        <Link 
                            to='/' 
                            className={`${buttonStyle} bg-blue-500`}
                            style={{textDecoration: 'none'}}
                        >
                            Home
                        </Link>
                       
                        {userContext.user ? (
                            <>
                                <Link 
                                    to='/publish' 
                                    className={`${buttonStyle} bg-green-500`}
                                    style={{textDecoration: 'none'}}
                                >
                                    Publish
                                </Link>
                                <Link 
                                    to='/profile' 
                                    className={`${buttonStyle} bg-purple-500`}
                                    style={{textDecoration: 'none'}}
                                >
                                    Profile
                                </Link>
                                <Link 
                                    to='/logout' 
                                    className={`${buttonStyle} bg-red-500`}
                                    style={{textDecoration: 'none'}}
                                >
                                    Logout
                                </Link>
                            </>
                        ) : (
                            <>
                                <Link 
                                    to='/login' 
                                    className={`${buttonStyle} bg-blue-500`}
                                    style={{textDecoration: 'none'}}
                                >
                                    Login
                                </Link>
                                <Link 
                                    to='/register' 
                                    className={`${buttonStyle} bg-green-500`}
                                    style={{textDecoration: 'none'}}
                                >
                                    Register
                                </Link>
                            </>
                        )}
                    </div>
                </nav>
            </div>
        </header>
    );
}

export default Header;
