import { useContext } from "react";
import { UserContext } from "../userContext";
import { Link } from "react-router-dom";

function Header(props) {
    const userContext = useContext(UserContext);
    
    return (
        <header className="bg-white shadow-sm p-4">
            <div className="container mx-auto flex flex-col md:flex-row justify-between items-center">
                <h1 className="text-2xl font-bold mb-4 md:mb-0">{props.title}</h1>
                
                <nav>
                    <div className="flex flex-wrap gap-2">
                        <Link to='/' className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 transition-colors">
                            Home
                        </Link>
                        
                        {userContext.user ? (
                            <>
                                <Link to='/publish' className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600 transition-colors">
                                    Publish
                                </Link>
                                <Link to='/profile' className="px-4 py-2 bg-purple-500 text-white rounded hover:bg-purple-600 transition-colors">
                                    Profile
                                </Link>
                                <Link to='/logout' className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600 transition-colors">
                                    Logout
                                </Link>
                            </>
                        ) : (
                            <>
                                <Link to='/login' className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 transition-colors">
                                    Login
                                </Link>
                                <Link to='/register' className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600 transition-colors">
                                    Register
                                </Link>
                            </>
                        )}
                    </div>
                </nav>
            </div>
        </header>
    );
}

export default Header;
