import React, { useState } from 'react';

function Photo(props) {
  const photo = props.photo || {};
  const [likeCount, setLikeCount] = useState(photo.likes || 0);

  const formatDate = (dateString) => {
    if (!dateString) return "Unknown date";
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const handleLike = () => {
    setLikeCount(prevCount => prevCount + 1);
  };

  const handleComment = () => {
    alert("Comment feature would open here!");
  };

  return (
    <div className="bg-white text-gray-800 mb-4 shadow-lg rounded-lg overflow-hidden">
      {/* Image */}
      <div className="relative h-48 w-full">
  <img
    className="absolute inset-0 w-full h-full object-cover object-center"
    src={photo.path ? "http://localhost:3001/" + photo.path : "https://placekitten.com/400/300"}
    alt={photo.name || "Photo"}
  />

  {/* Title overlay */}
  <div className="absolute bottom-0 left-0 w-full bg-white bg-opacity-70 px-3 py-2">
    <h4 className="text-lg font-bold">{photo.name || "Untitled Photo"}</h4>
  </div>
</div>


      {/* Details section */}
      <div className="bg-gray-50 p-4">
        {/* Caption */}
        <p className="text-gray-700 mb-3">{photo.comment || "No caption provided"}</p>

        {/* Info row */}
        <div className="flex flex-wrap justify-between text-sm text-gray-600 mb-3">
          <div className="flex items-center mr-4 mb-2">
            <span className="mr-1">üë§</span>
            <span>{photo.postedBy?.username || "Unknown user"}</span>
          </div>

          <div className="flex items-center mr-4 mb-2">
            <span className="mr-1">üïí</span>
            <span>{formatDate(photo.createdAt)}</span>
          </div>

          <div className="flex items-center mb-2">
            <span className="mr-1">üëÅÔ∏è</span>
            <span>{photo.views || 0}</span>
          </div>
        </div>

        {/* Like & Comment buttons */}
        <div className="flex justify-between border-t pt-3">
          <button 
            onClick={handleLike}
            className="flex items-center justify-center w-1/2 py-2 text-red-500 hover:bg-red-50 rounded-md transition-colors"
          >
            <span className="mr-1">‚ù§Ô∏è</span>
            <span>Like ({likeCount})</span>
          </button>

          <div className="border-r border-gray-300"></div>

          <button 
            onClick={handleComment}
            className="flex items-center justify-center w-1/2 py-2 text-blue-500 hover:bg-blue-50 rounded-md transition-colors"
          >
            <span className="mr-1">üí¨</span>
            <span>Comment ({photo.comments?.length || 0})</span>
          </button>
        </div>
      </div>
    </div>
  );
}

export default Photo;
